var sass = require('node-sass');
var fs = require('fs');

var argv = require('minimist')(process.argv.slice(2));

var entry = argv.entry || './src/style/all.scss';
var imagePath = argv.imagePath || '../src/images/';
var outPath =  argv.outPath || './lib/all.css';

sass.render({
  file: entry
  ,imagePath: imagePath
}, function(err, result) { 
	
	var batFileContentStr = result.css.toString();
				
	var buffer = new Buffer(batFileContentStr, "utf8");

	fs.writeFileSync(outPath, buffer, 'utf8');
});