var fs = require('fs');
var argv = require('minimist')(process.argv.slice(2));
var indexPath = argv.indexPath || "./lib/index.js";
var cssName = argv.cssName || './all.css';
var filterName = argv.filterName || '../src';

var contentText = fs.readFileSync(indexPath,'utf-8');

var index = contentText.indexOf("scss");

var start = contentText.indexOf(filterName);

var path = contentText.substring(start, index+4);

contentText = contentText.replace(path, cssName);

var buffer = new Buffer(contentText, "utf8");


fs.writeFileSync(indexPath, buffer, 'utf8');

