import React, {Component, PropTypes} from 'react'
import {render} from 'react-dom';

class ApproveButton extends Component {

	constructor(props){
		super(props);
	}
	goLeft(){
		this.props.goLeftHandle();
	}
	goRight(){
		this.props.goRightHandle();
	}

	render(){
		return (
				<div className='approve-button'>
					<div className="arrow-btn go-left" onClick={this.goLeft.bind(this)}></div>
					<div className="arrow-btn go-right" onClick={this.goRight.bind(this)}></div>
				</div>
		)
	}
}

module.exports = ApproveButton;