import React, {Component, PropTypes} from 'react'
import {render} from 'react-dom';

class NodeIframe extends Component {
	constructor(props){
		super(props);
	}
	render(){
		var width = window.innerWidth - 400;
		var height = window.innerHeight * 0.8 - 102 - 30;
		var style = {
			"width":"100%"
			,"height":height+"px"
			,"rameborder":0
			,"border":"none"
			, "min-height": "370px"
		}
		var closeButton = this.props.closePopUp ? <div><button onClick={this.props.closePopUp} /></div> : "";
		return (
				<div>
				<iframe rameborder={0} style={style} src={this.props.address}></iframe>
				{closeButton}
				</div>
			)
	}
}

module.exports = NodeIframe;