import React, {Component, PropTypes} from 'react';
import "../../src/loading/loading.scss";

export default class Loading extends Component{
	constructor(props){
		super(props)
	}
	render(){
		const _style = {
			background: 'rgba(128, 128, 128, 0.2)',
            position: 'absolute',
            width   : '100%',
            height  : '100%',
            top: 0,
            left: 0
		};
		let {hidden = false, style = {}} = this.props;
		let tempDis = hidden ? {display: 'none'} : {display: 'block'};
		let __style = Object.assign(tempDis, _style, style);
		return(
			<div className="spinner" style={__style}>
				<div className="spinner_con">
					<div className="bounce1"></div>
					<div className="bounce2"></div>
					<div className="bounce3"></div>
				</div>
			</div>
		)
	}
}